import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { Button } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { Checkbox, FormControlLabel } from '@material-ui/core';
import { addToCart, getCart, removeCart } from '../../Redux/Actions/CartAction';
import { addToOrder } from '../../Redux/Actions/OrderAction';
import Swal from 'sweetalert2';
import { Redirect } from 'react-router-dom';
import { fetchAddress, fetchAddressById } from '../../Redux/Actions/ProfileAction';
import Cookies from 'js-cookie';
import { DeleteOutlined } from '@ant-design/icons';

const items = [];

const Contents = () => {
    const dispatch = useDispatch();

    const cartList = useSelector((state) => state.cart.cartList);
    const orderSuccess = useSelector((state) => state.order.success);
    const loggedIn = useSelector((state) => state.user.loggedIn);
    const addressData = useSelector((state) => state.profile.addressList);

    const cookieCart = Cookies.get('cartList');
    const cookieId = Cookies.get('productId');

    const cartIsExist = cookieCart === undefined ? null : cookieCart;
    const idIsExist = cookieId === undefined ? null : cookieId;
    const parsedId = JSON.parse(idIsExist);

    console.log(parsedId);

    const fetchCookieCart = () => {
        dispatch(addToCart(
            { "product_id": parsedId }
        ));
    };

    const [update, setUpdate] = useState(false);
    const [ticked, setTicked] = useState({
        items: items,
    });

    const handleTickedChild = (e) => {
        const { id, checked } = e.target;
        if (checked) {
            items.push({
                product_id: id,
            });
        } else {
            for (let i = 0; i < items.length; i++) {
                if (items[i].product_id === id) {
                    items.splice(i, 1);
                }
            }
        }
        setTicked(items);
        console.log(ticked);
    };

    useEffect(() => {
        dispatch(getCart());
        if (update) {
            setUpdate(false);
        }
    }, [dispatch, update]);

    useEffect(() => {
        dispatch(fetchAddress());
    }, [dispatch]);

    useEffect(() => {
        dispatch(fetchAddressById(addressData._id));
    }, [dispatch, addressData._id]);

    const addressById = useSelector((state) => state.profile.addressById);

    const rounded = (num) => {
        let roundedString = num.toFixed(2);
        let round = Number(roundedString);
        return round;
    };

    const handleDelete = (id) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!',
        })
        .then((res) => {
            if (res.value) {
                Swal.fire(
                    'Deleted!',
                    'Product has been deleted.',
                    'success',
                );
                dispatch(removeCart(id));
                setUpdate(true);
                window.location.reload();
            }
        })
        .catch((err) => {
            console.log(err);
        })
    };

    const renderCart = () => {
        if (loggedIn) {
            return cartList.map((val,index) => {
                if (cartList.length === 0) {
                    return (
                        <tr>
                            <td>-</td>
                            <td>-</td>
                            <td>-</td>
                            <td>-</td>
                            <td>-</td>
                            <td>-</td>
                            <td>-</td>
                        </tr>
                    );
                }
                return (
                    <tr key={index} value={val.product_info}>
                        <td>
                            <input
                                type="checkbox"
                                id={val.product_info}
                                onChange={handleTickedChild}
                            />
                        </td>
                        <td>
                            {val.product_info.name}<br/>
                            TYPE : {val.product_info.type}
                        </td>
                        <td>
                            Rp. {val.product_info.price}
                        </td>
                        <td>
                            Hemat {rounded(100-((val.product_info.sale_price/val.product_info.price)*100))}%
                        </td>
                        <td>
                            Rp. {val.product_info.sale_price}
                        </td>
                        <td>
                            {
                                val.product_info.type === 'ecommerce'
                                ?
                                <input type='number' defaultValue={1} />
                                :
                                '1'
                            }
                        </td>
                        <td>
                            <Button 
                                danger 
                                icon={<DeleteOutlined/>} 
                                size='large' 
                                shape='circle' 
                                onClick={() => handleDelete(val.product_info)} 
                            />
                        </td>
                    </tr>
                );
            });
        } else {
            if (cartIsExist === null) {
                return (
                    <tr>
                        <td>-</td>
                        <td>-</td>
                        <td>-</td>
                        <td>-</td>
                        <td>-</td>
                        <td>-</td>
                    </tr>
                );
            }
            return JSON.parse(cartIsExist).map((val,index) => {
                return (
                    <tr key={index} value={val.product_info}>
                        <td>
                            {index + 1}
                        </td>
                        <td>
                            {val.name}<br/>
                            TYPE : {val.type}
                        </td>
                        <td>
                            Rp. {val.price.toLocaleString('id-ID')}
                        </td>
                        <td>
                            Hemat {rounded(100-((val.sale_price/val.price)*100))}%
                        </td>
                        <td>
                            Rp. {val.sale_price.toLocaleString('id-ID')}
                        </td>
                        <td>
                            {
                                val.type === 'ecommerce'
                                ?
                                <input type='number' defaultValue={1} />
                                :
                                '1'
                            }
                        </td>
                    </tr>
                );
            });
        }
    };

    const renderBump = () => {
        return cartList.map((val,index) => {
            const bumps = val.product_info.bump;
            return bumps.map((item, idx) => {
                return (
                    <div style={bump.box}>
                        <div style={bump.topSection}>
                            <FormControlLabel
                                control={
                                    <Checkbox
                                        color="primary"
                                        checked={tickedBump}
                                        onClick={handleChangeTicked}
                                    />
                                }
                                label={
                                    <span style={{fontSize: '1.15rem', fontWeight:'500'}}>
                                        {item.bump_name}
                                    </span>
                                }
                                style={bump.tickBump}
                            />
                        </div>
                        <div style={bump.content}>
                            <img src={item.bump_image} alt='gambar bump' style={bump.contentImage} />
                            <div style={bump.contentText}>
                                <div style={bump.contentTitle}>
                                    {item.bump_name}
                                </div>
                                <div style={bump.contentDescription}>
                                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec semper tincidunt sodales. Vestibulum venenatis porttitor lorem, quis finibus velit viverra ac. Ut a varius nulla. Nam tempus dapibus leo non vulputate. Fusce id mi ipsum. Nam in dui laoreet, lobortis ligula at, scelerisque mi. Cras nec nunc magna. Proin blandit viverra dui. Fusce tempor dignissim lorem vel tincidunt.
                                </div>
                            </div>
                        </div>
                        <div style={bump.bottomSection}>
                            <span style={bump.footerTxt}>
                                Footer for : {item.bump_name}
                            </span>
                        </div>
                    </div>
                );
            });
        });
    };

    const [tickedBump, setTickedBump] = useState(false);

    const handleChangeTicked = (e) => {
        setTickedBump(
            !tickedBump,
            e.target.checked,
        );
    };

    let totalCourse = cartList.length;
    let price = 0;
    let salePrice = 0;

    // if ('price' in cartList && 'sale_price' in cartList) {
        cartList.map((val,index) => {
            return(
                <div key={index}>
                    <div>
                        {price += val.product_info.price}
                    </div>
                    <div>
                        {salePrice += val.product_info.sale_price}
                    </div>
                </div>
            );
        });
    // }

    const handleStoreOrder = () => {
        dispatch(addToOrder(
            {
                items: ticked,
                shipment: {
                    address_id: `${addressById._id} address id reference from user address`
                }
            }
        ));
        console.log(
            `
                {
                    items: ${ticked},
                    shipment: {
                        address_id: "${addressById._id} address id reference from user address"
                    }
                }
            `
        );
    };

    if (orderSuccess) {
        return (
            <Redirect to='payment' />
        );
    }
    return (
        <React.Fragment>
            {
                loggedIn
                ?
                <>
                    {/* SHOPPING CART */}
                    <div style={cart.container}>
                        <div style={cart.title}>
                            Kelas yang Anda Ikuti
                        </div>
                        {
                            cookieCart.length !== 0
                            ?
                            <div style={{display:'flex', justifyContent:'center', marginBottom:'1.5rem'}}>
                                <Button onClick={fetchCookieCart}>
                                    Fetch my items from cookies
                                </Button>
                            </div>
                            :
                            null
                        }
                        <div>
                            <Table bordered hover>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Product Name</th>
                                        <th>Price</th>
                                        <th>Discount</th>
                                        <th>Sale Price</th>
                                        <th>Quantity</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {renderCart()}
                                </tbody>
                            </Table>
                        </div>
                    </div>
                    {/* ORDER BUMP */}
                    <div>
                        <div style={bump.separator} />
                        {renderBump()}
                    </div>
                    {/* SUMMARY */}
                    <div style={summary.container}>
                        <div style={summary.content}>
                            <div style={summary.names}>
                                <div style={summary.namesTxt}>
                                    Course Taken
                                </div>
                                <div style={summary.namesTxt}>
                                    Total Price
                                </div>
                                <div style={summary.namesTxt}>
                                    Total Sale Price
                                </div>
                                <div style={summary.namesTxt}>
                                    You Saved
                                </div>
                            </div>
                            <div style={summary.values}>
                                <div style={summary.valuesTxt}>
                                    {totalCourse}
                                </div>
                                <div style={summary.valuesTxt}>
                                    Rp. {price.toLocaleString('id-ID')}
                                </div>
                                <div style={summary.valuesTxt}>
                                    Rp. {salePrice.toLocaleString('id-ID')}
                                </div>
                                <div style={summary.valuesTxt}>
                                    Rp. {(price - salePrice).toLocaleString('id-ID')}
                                </div>
                            </div>
                        </div>
                        <div style={summary.separator1} />
                        <div style={summary.pay}>
                            <div style={summary.payTxt}>
                                Amount will be paid
                            </div>
                            <div style={summary.payNum}>
                                Rp. {salePrice.toLocaleString('id-ID')}
                            </div>
                        </div>
                        <div style={summary.separator1} />
                        <div style={summary.choosePayBtn} onClick={handleStoreOrder}>
                            <div style={summary.btnTxt}>
                                PILIH METODE PEMBAYARAN
                            </div>
                        </div>
                    </div>
                </>
                :
                <>
                    {/* SHOPPING CART */}
                    <div style={cart.container}>
                        <div style={cart.title}>
                            Kelas yang Anda Ikuti
                        </div>
                        <div>
                            <Table bordered hover>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Product Name</th>
                                        <th>Price</th>
                                        <th>Discount</th>
                                        <th>Sale Price</th>
                                        <th>Quantity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {renderCart()}
                                </tbody>
                            </Table>
                        </div>
                    </div>
                </>
            }
        </React.Fragment>
    );
};

const cart = {
    container: {
        margin: '0rem 10rem',
    },
